var Scene = (function(){


	function Scene(player, sceneManager){

		// TODO instead of passing in player, pass in a scene options object (generated by the SceneManager)
		/*
		{
			player: player,
			sceneName: sceneName,
			nextScene: 'someNextScene',
			previousScene: 'someSceneYouCameFrom',
			etc: andSoOn
		}
		*/
		this.player = player;
	}

	$.extend(Scene.prototype, {
		startScene:function(){
			sceneManager.createScene(this.name);
		},
		endScene:function(){
			sceneManager.destroyScene(this);
		}
	});

	return Scene;

})();